import React, { Component } from "react";

import Person from "../components/Person/Person";
import AddPerson from "../components/AddPerson/AddPerson";
import { connect } from 'react-redux';

class Persons extends Component {


    // personAddedHandler = () => {
    //   const newPerson = {
    //     id: Math.random(), // not really unique but good enough here!
    //     name: "Max",
    //     age: Math.floor(Math.random() * 40)
    //   };
    //   this.setState(prevState => {
    //     return { persons: prevState.props.prsns.concat(newPerson) };
    //   });
    // };

    personDeletedHandler = personId => {
      this.setState(prevState => {
        return {
          persons: prevState.persons.filter(person => person.id !== personId)
        };
      });
    };

  render() {
    return (
      <div>
        <AddPerson personAdded={this.props.personAdd} />
        {this.props.prsns.map(person => (
          <Person
            key={person.id}
            name={person.name}
            age={person.age}
            clicked={id => this.personDeletedHandler(person.id)}
          />
        ))}
      </div>
    );
  }
}


const mapStateToProps = state =>{
   return {
       prsns: [...state.persons]
   } 
}

const mapDispatchToProps = dispatch => {
    return{
    personAdd: () => dispatch({type:"ADD"})}
}

export default connect(mapStateToProps, mapDispatchToProps)(Persons);
